import React from "react";
import { CurrentUserContext } from "../contexts/CurrentUserContext";
import PopupWithForm from "./PopupWithForm";

function EditAvatarPopup({ isOpen, onClose, onUpdateAvatar }) {
  const currentUser = React.useContext(CurrentUserContext);
  const profilePhoto = React.useRef(); ///Что в скобках

  function handleSubmit(event) {
    event.preventDefault();
    console.log("ava");

    onUpdateAvatar({
      avatar: profilePhoto.current.value,
    });
  }

  React.useEffect(() => {
    profilePhoto.current.value = currentUser.avatar;
  }, [currentUser]);

  console.log(profilePhoto.current.value);

  return (
    <PopupWithForm
      name="photo-edit"
      title="Обновить аватар"
      children=""
      isOpen={isOpen}
      onClose={onClose}
      buttonText="Сохранить"
      handleSubmit={handleSubmit}
    >
      <input
        type="url"
        className="popup__input popup__input_type_photo"
        name="photo-url"
        id="photo-url-input"
        placeholder="Ссылка на фото"
        handleSubmit={handleSubmit}
        ref={profilePhoto}
        required
      />
      <span className="popup__input-error" id="photo-url-input-error"></span>
    </PopupWithForm>
  );
}

export default EditAvatarPopup;
